import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent implements OnInit {

  public eventos: any = [];
/*Determino a variável para o tamanho da imagem e a condição para ela aparecer*/
  widthImg: number = 150;
  marginImg: number = 2;
  showImg: boolean = true;
  private _filtroLista: string = '';

  public get filtroLista(): string{
    return this._filtroLista;
  }

  public set filtroLista(value: string){
    this._filtroLista = value;
    this.eventos = this.filtroLista ? this.filtrarEventos(this.filtroLista) : this.eventos
  }

  filtrarEventos(filtrarPor: string) : any {
    filtrarPor = filtrarPor.toLocaleLowerCase();
    return this.eventos.filter(
      thievento => evento.tema.toLocaleLowerCase().indexOf(filtrarPor) !== -1
    )
  }

  constructor(private http: HttpClient) { }
  isCollapsed = true;

  alterarImagem(){
    this.showImg = !this.showImg;
  }

  ngOnInit(): void {
     this.getEventos();
  }

  public getEventos(): void {
    this.http.get('https://localhost:5001/api/eventos').subscribe(
      response => this.eventos = response,
      error => console.log(error)
    );
  }
}
<h2>Eventos</h2>
<hr />

<div class="form-inline">
  <div class="input-group mb-3">
    <input 
    type="text" 
    class="form-control mr-2" 
    placeholder="buscar"
    [(ngModel)]="filtroLista"
    />
    <button class="btn btn-outline-success">BUSCAR</button>
  </div>
</div>
<h3>Filtro: {{filtroLista}}</h3>
<table class="table table-light table-striped">
  <thead class="table-dark">
    <tr>
      <th scope="col">
        <button
          typex="button"
          class="btn btn-outline-light btn-sm"
          (click)="alterarImagem()"
        >
          {{ !showImg ? "Mostrar" : "Ocultar" }}
        </button>
      </th>
      <th scope="col">#</th>
      <th scope="col">Tema</th>
      <th scope="col">Local</th>
      <th scope="col">Data</th>
      <th scope="col">Qtde Pessoas</th>
      <th scope="col">Lote</th>
      <th scope="col">Opções</th>
    </tr>
  </thead>
  <tbody *ngIf="eventos && eventos.length">
    <tr *ngFor="let evento of eventos">
      <td [collapse]="showImg"
            [isAnimated]="true">
          <img
            src="../assets/{{ evento.imagemURL }}"
            [style.width.px]="widthImg"
            [style.margin.px]="marginImg"
          />
        </td>
      <td>{{ evento.eventoId }}</td>
      <td>{{ evento.tema }}</td>
      <td>{{ evento.local }}</td>
      <td>{{ evento.dataEvento }}</td>
      <td>{{ evento.qtdePessoas }}</td>
      <td>{{ evento.lote }}</td>
      <td></td>
    </tr>
  </tbody>
  <tfoot *ngIf="eventos && !eventos.length">
    <tr>
      <td colspan="8" class="text-center">
        <h4>Nenhum evento encontrado!</h4>
      </td>
    </tr>
  </tfoot>
</table>
