import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent implements OnInit {

  public eventos: any = [];
/*Determino a variável para o tamanho da imagem e a condição para ela aparecer*/
  widthImg: number = 150;
  marginImg: number = 2;
  showImg: boolean = true;

  constructor(private http: HttpClient) { }
  isCollapsed = true;

  alterarImagem(){
    this.showImg = !this.showImg;
  }

  ngOnInit(): void {
     this.getEventos();
  }

  public getEventos(): void {
    this.http.get('https://localhost:5001/api/eventos').subscribe(
      response => this.eventos = response,
      error => console.log(error)
    );
  }
}
<h2>Eventos</h2>
<hr />

<form action="" class="form-inline">
  <div class="input-group mb-3">
    <input type="text" class="form-control" placeholder="buscar" />
    <button class="btn btn-outline-success">BUSCAR</button>
  </div>
</form>

<table class="table table-light table-stripped">
  <thead class="table-dark">
    <tr>
      <th scope="col">#</th>
      <th scope="col">
        <button
          typex="button"
          class="btn btn-outline-light btn-sm"
          (click)="alterarImagem()"
        >
          {{ !showImg ? "Mostrar" : "Ocultar" }}
        </button>
      </th>
      <th scope="col">Tema</th>
      <th scope="col">Local</th>
      <th scope="col">Data</th>
      <th scope="col">Qtde Pessoas</th>
      <th scope="col">Lote</th>
      <th scope="col">Opções</th>
    </tr>
  </thead>
  <tbody *ngIf="eventos && eventos.length">
    <tr *ngFor="let evento of eventos">
      <td>{{ evento.eventoId }}</td>

      <td>{{ evento.tema }}</td>
      <td>{{ evento.local }}</td>
      <td>{{ evento.dataEvento }}</td>
      <td>{{ evento.qtdePessoas }}</td>
      <td>{{ evento.lote }}</td>
      <td></td>
    </tr>
  </tbody>
  <tfoot *ngIf="eventos && !eventos.length">
    <tr>
      <td colspan="8" class="text-center">
        <h4>Nenhum evento encontrado!</h4>
      </td>
    </tr>
  </tfoot>
</table>
